mapper.update:
UPDATE BOARD
SET BCOUNT = (SELECT BCOUNT FROM BOARD WHERE BOARD_ID = 'B' || #{board_id}) + 1
WHERE BOARD_ID = 'B' || #{board_id}

mapper.select:
SELECT THEATER_ID
FROM THEATER
WHERE MID = #{mid}

mapper.update:
